INSERT INTO `spip_forum` (`id_forum`, `id_objet`, `objet`, `id_parent`, `id_thread`, `date_heure`, `date_thread`, `titre`, `texte`, `auteur`, `email_auteur`, `nom_site`, `url_site`, `statut`, `ip`, `maj`, `id_auteur`, `composition`, `composition_lock`) VALUES
(1, 16, 'article', 0, 1, '2015-09-16 09:13:52', '2015-09-21 13:32:35', 'question : SOS construction', 'Alors, moi je crois que c''est une maison, une pas drôle parce qu''il n''y a pas de fenêtres ! Tu peux mettre des fenêtres s''il te plaît ? \r\nEt puis à droite, je vois un crocodile ! Si, si ! ', 'Yanne', 'yanne.vb@gmail.com', '', '', 'publie', '565cf77b2194b3beae13331ab34429d6', '2017-01-21 19:37:19', 4, '', 0),
(2, 10, 'article', 0, 2, '2015-09-16 11:03:55', '2015-09-17 13:50:18', 'suite du loup et du dragon', 'Merci Orgès pour cette histoire. \r\n\r\nJe suis un peu déçu car j''aime bien les loups.\r\nEst ce que tu pourrais nous proposer une histoire de loup sympathique ?\r\n\r\nErwan', 'erwan', 'redonerwan@orange.fr', '', '', 'publie', 'f9c014ef08e28c77ead9e0f9699d9195', '2017-01-21 19:37:19', 5, '', 0),
(3, 16, 'article', 1, 1, '2015-09-16 13:37:44', '2015-09-21 13:32:35', 'question : SOS construction', 'Ce n''est pas un crocodile, c''est un avion. C''est Antonio qui l''a fait.\r\nLa maison n''a pas de fenêtre parce que personne ne va habiter dedans !', 'Magali', 'magali.b@free.fr', 'réponse à Yanne', '', 'publie', '328887e6eabe21b17bd68fd2908df898', '2017-01-21 19:37:19', 6, '', 0),
(4, 18, 'article', 0, 4, '2015-09-17 13:31:14', '0000-00-00 00:00:00', 'sos théatre ', 'Bonjour,\r\nBravo pour ce beau projet !\r\nJe veux bien venir vous aider à créer votre pièce de théâtre avec marionnettes. Faire des spectacles, c''est mon métier. Dites-moi quand vous voulez que je vienne !\r\nMagali', 'Magali', 'magali.b@free.fr', '', '', 'off', '328887e6eabe21b17bd68fd2908df898', '2017-01-21 19:37:19', 0, '', 0),
(5, 18, 'article', 0, 5, '2015-09-17 13:33:20', '2015-09-17 14:25:31', 'sos théatre ', 'Bonjour,\r\nbravo pour ce beau projet !\r\nJe veux bien venir vous aider à monter votre spectacle de théâtre avec des marionnettes. Créer des spectacles, c''est mon métier.\r\nSI ça vous va, dites-moi quand vous voulez que je vienne !\r\nMagali', 'Magali', 'magali.b@free.fr', '', '', 'publie', '328887e6eabe21b17bd68fd2908df898', '2017-01-21 19:37:19', 6, '', 0),
(6, 14, 'article', 0, 6, '2015-09-17 13:45:31', '2015-09-29 16:59:46', 'L''oiseau, par Flora', 'Ca c''est une belle histoire d''oiseau ! Moi je n''aime pas les oiseaux en cage, même si à l''extérieur ils peuvent de faire manger par des chats ou d''autres prédateurs.\r\nMagali', 'Magali', 'magali.b@free.fr', '', '', 'publie', '328887e6eabe21b17bd68fd2908df898', '2017-01-21 19:37:19', 6, '', 0),
(7, 18, 'article', 5, 5, '2015-09-17 13:49:25', '2015-09-17 14:25:31', 'sos théatre ', 'merci. Avec plaisir. Peux tu venir demain matin ?\r\n\r\nFlora, Orgès, Lilith', 'Les Bricabracs', 'lesbricabracs@bricabracs.org', '', '', 'publie', '3e11f17e5fda44850342f6781c56dd05', '2017-01-21 19:37:19', 3, '', 0),
(8, 10, 'article', 2, 2, '2015-09-17 13:50:18', '2015-09-17 13:50:18', 'suite du loup et du dragon', 'Moi aussi j''aime bien les loups...et aussi les dragons. Ca me plairait aussi de lire une histoire de loup sympa !\r\nMagali', 'Magali', 'magali.b@free.fr', '', '', 'publie', '328887e6eabe21b17bd68fd2908df898', '2017-01-21 19:37:19', 6, '', 0),
(9, 18, 'article', 7, 5, '2015-09-17 13:51:56', '2015-09-17 14:25:31', 'sos théatre ', 'D''accord ! Je viendrai avec Flora pour 9h.\r\nMagali', 'Magali', 'magali.b@free.fr', '', '', 'publie', '328887e6eabe21b17bd68fd2908df898', '2017-01-21 19:37:19', 6, '', 0),
(10, 13, 'article', 0, 10, '2015-09-17 13:56:45', '2015-09-17 13:56:45', 'calcul de carreaux', 'Peut-être en mesurant un carreau puis toute la terrasse ?', 'Magali', 'magali.b@free.fr', '', '', 'publie', '328887e6eabe21b17bd68fd2908df898', '2017-01-21 19:37:19', 6, '', 0),
